import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const LoginPage = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    remember: false
  });

  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    
    // Simulate API call
    setTimeout(() => {
      console.log('Login attempt:', formData);
      setIsLoading(false);
      // Here you would typically make an API call to authenticate the user
    }, 2000);
  };

  const handleSocialLogin = (provider) => {
    console.log(`${provider} login clicked`);
    // Here you would implement social login logic
  };

  return (
    <div className="container">
      <div className="form-container">
        <div className="form-header">
          <h1>Welcome Back</h1>
          <p>Sign in to your account to continue</p>
        </div>
        
        <form className="login-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <div className="input-icon">
              <i className="fas fa-envelope"></i>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="Email address"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
          </div>
          
          <div className="form-group">
            <div className="input-icon">
              <i className="fas fa-lock"></i>
              <input
                type="password"
                id="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>
          </div>
          
          <div className="form-options">
            <label className="checkbox-container">
              <input
                type="checkbox"
                name="remember"
                checked={formData.remember}
                onChange={handleChange}
              />
              <span className="checkmark"></span>
              Remember me
            </label>
            <a href="#" className="forgot-password">Forgot password?</a>
          </div>
          
          <button 
            type="submit" 
            className={`btn btn-primary ${isLoading ? 'loading' : ''}`}
            disabled={isLoading}
          >
            {!isLoading && <i className="fas fa-sign-in-alt"></i>}
            {isLoading ? 'Signing In...' : 'Sign In'}
          </button>
        </form>
        
        <div className="divider">
          <span>or</span>
        </div>
        
        <div className="social-login">
          <button 
            className="btn btn-social btn-google"
            onClick={() => handleSocialLogin('Google')}
          >
            <i className="fab fa-google"></i>
            Continue with Google
          </button>
          <button 
            className="btn btn-social btn-linkedin"
            onClick={() => handleSocialLogin('LinkedIn')}
          >
            <i className="fab fa-linkedin"></i>
            Continue with LinkedIn
          </button>
        </div>
        
        <div className="form-footer">
          <p>Don't have an account? <Link to="/signup">Sign up here</Link></p>
        </div>
      </div>
      
      <div className="image-container">
        <div className="overlay"></div>
        <div className="content">
          <h2>Find Your Dream Job</h2>
          <p>Connect with top employers and discover opportunities that match your skills and aspirations.</p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
