import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    rollNumber: '',
    userType: '',
    terms: false,
    newsletter: false
  });

  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.firstName.trim()) newErrors.firstName = 'First name is required';
    if (!formData.lastName.trim()) newErrors.lastName = 'Last name is required';
    if (!formData.email.trim()) newErrors.email = 'Email is required';
    else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Email is invalid';
    if (!formData.phone.trim()) newErrors.phone = 'Phone number is required';
    if (!formData.rollNumber.trim()) newErrors.rollNumber = 'Roll number is required';
    if (!formData.userType) newErrors.userType = 'Please select an account type';
    if (!formData.terms) newErrors.terms = 'You must agree to the terms of service';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    setIsLoading(true);
    setTimeout(() => {
      console.log('Register attempt:', formData);
      setIsLoading(false);
    }, 2000);
  };

  const handleSocialRegister = (provider) => {
    console.log(`${provider} register clicked`);
  };

  return (
    <div className="container">
      <div className="form-container">
        <div className="form-header">
          <h1>Register Account</h1>
          <p>Join thousands of professionals and find your next opportunity</p>
        </div>
        <form className="register-form" onSubmit={handleSubmit}>
          <div className="form-row">
            <div className="form-group">
              <div className="input-icon">
                <i className="fas fa-user"></i>
                <input
                  type="text"
                  id="firstName"
                  name="firstName"
                  placeholder="First name"
                  value={formData.firstName}
                  onChange={handleChange}
                  className={errors.firstName ? 'error' : ''}
                  required
                />
              </div>
              {errors.firstName && <span className="error-message">{errors.firstName}</span>}
            </div>
            <div className="form-group">
              <div className="input-icon">
                <i className="fas fa-user"></i>
                <input
                  type="text"
                  id="lastName"
                  name="lastName"
                  placeholder="Last name"
                  value={formData.lastName}
                  onChange={handleChange}
                  className={errors.lastName ? 'error' : ''}
                  required
                />
              </div>
              {errors.lastName && <span className="error-message">{errors.lastName}</span>}
            </div>
          </div>
          <div className="form-group">
            <div className="input-icon">
              <i className="fas fa-envelope"></i>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="Email address"
                value={formData.email}
                onChange={handleChange}
                className={errors.email ? 'error' : ''}
                required
              />
            </div>
            {errors.email && <span className="error-message">{errors.email}</span>}
          </div>
          <div className="form-group">
            <div className="input-icon">
              <i className="fas fa-phone"></i>
              <input
                type="tel"
                id="phone"
                name="phone"
                placeholder="Phone number"
                value={formData.phone}
                onChange={handleChange}
                className={errors.phone ? 'error' : ''}
                required
              />
            </div>
            {errors.phone && <span className="error-message">{errors.phone}</span>}
          </div>
          <div className="form-group">
            <div className="input-icon">
              <i className="fas fa-id-card"></i>
              <input
                type="text"
                id="rollNumber"
                name="rollNumber"
                placeholder="Roll number"
                value={formData.rollNumber}
                onChange={handleChange}
                className={errors.rollNumber ? 'error' : ''}
                required
              />
            </div>
            {errors.rollNumber && <span className="error-message">{errors.rollNumber}</span>}
          </div>
          <div className="form-group">
            <select
              id="userType"
              name="userType"
              className={`form-select ${errors.userType ? 'error' : ''}`}
              value={formData.userType}
              onChange={handleChange}
              required
            >
              <option value="">Select account type</option>
              <option value="jobseeker">Job Seeker</option>
              <option value="employer">Employer</option>
              <option value="recruiter">Recruiter</option>
            </select>
            {errors.userType && <span className="error-message">{errors.userType}</span>}
          </div>
          <div className="form-options">
            <label className="checkbox-container">
              <input
                type="checkbox"
                name="terms"
                checked={formData.terms}
                onChange={handleChange}
                required
              />
              <span className="checkmark"></span>
              I agree to the <a href="#" className="terms-link">Terms of Service</a> and <a href="#" className="terms-link">Privacy Policy</a>
            </label>
            {errors.terms && <span className="error-message">{errors.terms}</span>}
            <label className="checkbox-container">
              <input
                type="checkbox"
                name="newsletter"
                checked={formData.newsletter}
                onChange={handleChange}
              />
              <span className="checkmark"></span>
              Subscribe to our newsletter for job updates
            </label>
          </div>
          <button 
            type="submit" 
            className={`btn btn-primary ${isLoading ? 'loading' : ''}`}
            disabled={isLoading}
          >
            {!isLoading && <i className="fas fa-user-plus"></i>}
            {isLoading ? 'Registering...' : 'Register'}
          </button>
        </form>
        <div className="divider">
          <span>or</span>
        </div>
        <div className="social-login">
          <button 
            className="btn btn-social btn-google"
            onClick={() => handleSocialRegister('Google')}
          >
            <i className="fab fa-google"></i>
            Register with Google
          </button>
          <button 
            className="btn btn-social btn-linkedin"
            onClick={() => handleSocialRegister('LinkedIn')}
          >
            <i className="fab fa-linkedin"></i>
            Register with LinkedIn
          </button>
        </div>
        <div className="form-footer">
          <p>Already have an account? <Link to="/login">Sign in here</Link></p>
        </div>
      </div>
      <div className="image-container">
        <div className="overlay"></div>
        <div className="content">
          <h2>Start Your Journey</h2>
          <p>Create your profile and unlock access to thousands of job opportunities from leading companies worldwide.</p>
        </div>
      </div>
    </div>
  );
};

export default RegisterPage;
